<?	include_once 'model/User.php';	abstract class C_Base extends C_Controller {				protected $title;		protected $description;				protected function before() {						$this->title = 'Shop-online';			$this->description = '';		}		public function render() {						$get_user = new User();			if (isset($_SESSION['id_user'])) {				$user_info = $get_user->getUser($_SESSION['id_user']);			} else {				$user_info['name'] = false;			}			$vars = ['title' => $this->title, 'description' => $this->description, 'user' => $user_info, 'date' => date("d.m.Y")];			$page = $this->Template('view/v_main.php', $vars);			echo $page;		}	}