<?	include_once 'model/Product.php';	include_once 'model/Basket.php';	class C_Page extends C_Base {				public function action_index() {						$this->title .= ' | Главная';			$this->description = $this->Template('view/v_index.php', []);			}		public function action_catalog() {						$product_object = new Product();			$catalog = $product_object->getAllProducts();			$this->title .= ' | Каталог';			$this->description = $this->Template('view/v_catalog.php', ['catalog' => $catalog]);		}		public function action_product($id) {			$product_object = new Product();			$product = $product_object->getProduct($id);			if (isset($_SESSION["id_user"])) {				$user_id = $_SESSION["id_user"];			} else {				$user_id = false;			}			$this->title .= ' | ' . $product['title'];			$this->description = $this->Template('view/v_product.php', ['product' => $product, 'user_id' => $user_id]);			if($this->isPost()) {				$new_basket = new Basket();				$result = $new_basket->addProduct($product['id'], $user_id, $_POST['count']);				$this->description = $this->Template('view/v_product.php', ['product' => $product, 'user_id' => $user_id, 'text' => $result]);			}		}		public function action_basket() {						if (isset($_SESSION['id_user'])) {				$user_id = $_SESSION['id_user'];			} else {				$user_id = false;			}			$basket_object = new Basket();			$basket = $basket_object->getBasket($user_id);			$this->title .= ' | Корзина заказов';			$this->description = $this->Template('view/v_basket.php',['products' => $basket]);		}	}